mapper.select:
select b.*, c.name as category_name from book b left join category c on b.category_id = c.id
and b.title like concat('%', #{title}, '%')
and b.author like concat('%', #{author}, '%')
and b.category_id = #{categoryId}
order by b.create_time desc limit #{offset}, #{pageSize}

mapper.update:
update book
title = #{title}, author = #{author}, isbn = #{isbn}, category_id = #{categoryId}, publisher = #{publisher}, publish_date = #{publishDate}, price = #{price}, stock = #{stock}, description = #{description}, cover = #{cover}, update_time = now()
where id = #{id}
